CREATE TABLE graph_title (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title_en` varchar(255) DEFAULT NULL,
  `title_pt` varchar(255) DEFAULT NULL,
  `subtitle_en` varchar(255) DEFAULT NULL,
  `subtitle_pt` varchar(255) DEFAULT NULL,
  `product` tinyint(1) DEFAULT '0',
  `partner` tinyint(1) DEFAULT '0',
  `type` varchar(45) DEFAULT NULL,
  `occupation` tinyint(1) DEFAULT '0',
  `industry` tinyint(1) DEFAULT '0',
  `location` tinyint(1) DEFAULT '0',
  `shapes` varchar(45) DEFAULT NULL,
  `graph` varchar(45) DEFAULT NULL,
  `dataset` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=latin1;

